import { ChangeEvent, FormEvent, LegacyRef, useRef, useState } from 'react';
import './App.css';

function App() {
  const [file, setFile] = useState<File>();
  const fileSelector: LegacyRef<HTMLInputElement> = useRef(null);

  const handleFileSelection = (): void => {
    fileSelector?.current?.click();
  };

  const handleClearFile = (): void => {
    setFile(undefined);
  };

  const handleSetFile = (e: ChangeEvent<HTMLInputElement>): void => {
    e.preventDefault();
    e.stopPropagation();

    const files = e.target.files;
    if (files && files.length > 0) {
      setFile(files[0]);
    }
  };

  const handleFormSubit = (e: FormEvent<HTMLFormElement>): void => {
    e.preventDefault();
    e.stopPropagation();

    console.log('Handling file upload: ', file?.name);
  };

  return (
    <>
      <h1>RaspPrinter</h1>
      <form onSubmit={handleFormSubit}>
        <div className="drop-box">
          <input
              type='file'
              ref={fileSelector}
              onChange={handleSetFile}
              accept='.pdf, .odt, .doc, .docx, image/*'
            />
            <label htmlFor='fileInput'>
              {
                file ? file.name : 'Select or drop a file'
              }
            </label>
            <button
              className='select-button'
              name='fileInput'
              id='fileInput'
              onClick={handleFileSelection}>
                Select
            </button>
            <button className='clear-button'
              name='clearFile'
              id='clearFile'
              onClick={handleClearFile}>
              Clear
            </button>
        </div>

        <button className='print-btn' type='submit'>Print</button>
      </form>
    </>
  );
}

export default App;
