import { ChangeEvent, FormEvent, LegacyRef, useRef, useState } from 'react';
import './App.css';

function App() {
  const [file, setFile] = useState<File>();
  const fileSelector: LegacyRef<HTMLInputElement> = useRef(null);


  const handleFileSelection = () => {
    fileSelector?.current?.click();
  };

  const handleSetFile = (e: ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    const files = e.target.files;
    if (files && files.length > 0) {
      setFile(files[0]);
    }
  };

  const handleFormSubit = (e: FormEvent<HTMLFormElement>): void => {
    e.preventDefault();
    console.log('Handling file upload: ', file?.name);
  };

  return (
    <>
      <h1>RaspPrinter</h1>
      <form onSubmit={handleFormSubit}>
        <div className="drop-box">
        <input
            type='file'
            ref={fileSelector}
            name='fileInput'
            id='fileInput'
            onChange={handleSetFile}
            accept='.pdf, .odt, .doc, .docx, image/*'
          />
          <label htmlFor='fileInput'>
            {
              file ? file.name : 'Select or drop a file'
            }
          </label>
          <button className='select-button' onClick={handleFileSelection}>Select a File...</button>

        </div>
        <button type='submit'>Print</button>
      </form>
    </>
  );
}

export default App;
