import { ChangeEvent, FormEvent, useState } from 'react';
import './App.css';

function App() {
  const [file, setFile] = useState<File>();


  const handleSetFile = (e: ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    const files = e.target.files;
    if (files && files.length > 0) {
      setFile(files[0]);
    }
  };

  const handleFormSubit = (e: FormEvent<HTMLFormElement>): void => {
    e.preventDefault();
    console.log('Handling file upload: ', file?.name);
  };

  return (
    <>
      <h1>RaspPrinter</h1>
      <form onSubmit={handleFormSubit}>
        <div className="drop-box">
          <label htmlFor='fileInput'>Select or drop a file</label>
          <input type="file" id='fileInput' datatype='pdf, csv, txt, doc, docx, jpg, png' onChange={handleSetFile}/>
          { file && <div>{file.name}</div> }
        </div>
        <button type='submit'>Print</button>
      </form>
    </>
  );
}

export default App;
