import { ChangeEvent, LegacyRef, useEffect, useRef, useState } from 'react';
import axios from 'axios';
import './App.css';

const PRINT_API_URL = 'http://localhost:5000/printer/print';

function App() {
  const [file, setFile] = useState<File>();
  const fileSelector: LegacyRef<HTMLInputElement> = useRef(null);

  useEffect(() => {
    axios.defaults.headers.post['Access-Control-Allow-Origin'] = '*';
  },[])

  const handleFileSelection = (): void => {
    fileSelector?.current?.click();
  };

  const handleClearFile = (): void => {
    setFile(undefined);
  };

  const handleSetFile = (e: ChangeEvent<HTMLInputElement>): void => {
    const files = e.target.files;

    if (files && files.length > 0) {
      setFile(files[0]);
    }
  };

  const handleFormSubit = (): void => {
    console.log('Handling file upload: ', file?.name);
    console.log(file);
    axios
      .post(
        PRINT_API_URL,
        { file }
      ).then((response) => {
        console.log(response);
      }).catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <h1>RaspPrinter</h1>
      <form>
        <div className="drop-box">
          <input
              type='file'
              ref={fileSelector}
              onChange={handleSetFile}
              accept='.pdf, .odt, .doc, .docx, image/*'
            />
            <span className='file-name'>{ file ? file.name : 'Select or drop a file' }</span>
            <div className="action-buttons">
              <button className='clear-button'
                name='clearFile'
                id='clearFile'
                onClick={handleClearFile}>
                Clear
              </button>
              <button
                className='select-button'
                name='fileInput'
                id='fileInput'
                onClick={handleFileSelection}>
                  Select
              </button>
            </div>
        </div>

        <button className='print-btn' type='submit' onClick={handleFormSubit}>Print</button>
      </form>
    </>
  );
}

export default App;
