import { ChangeEvent, LegacyRef, MouseEvent, useRef, useState } from 'react';
import './App.css';

function App() {
  const [file, setFile] = useState<File | null>();
  const fileSelector: LegacyRef<HTMLInputElement> = useRef(null);

  const handleFileSelection = (e: MouseEvent<HTMLButtonElement>): void => {
    e.preventDefault();
    e.stopPropagation();
    fileSelector?.current?.click();
  };

  const handleClearFile = (e: MouseEvent<HTMLButtonElement>): void => {
    e.preventDefault();
    e.stopPropagation();
    setFile(null);
  };

  const handleSetFile = (e: ChangeEvent<HTMLInputElement>): void => {
    e.preventDefault();
    e.stopPropagation();

    const files = e.target.files;

    if (files && files.length > 0) {
      setFile(files[0]);
    }
  };

  const handleFormSubit = (e: MouseEvent<HTMLButtonElement>): void => {
    e.preventDefault();
    e.stopPropagation();
    console.log('Handling file upload: ', file?.name);
  };

  return (
    <>
      <h1>RaspPrinter</h1>
      <form>
        <div className="drop-box">
          <input type='file' ref={fileSelector} onChange={handleSetFile}
              accept='.pdf, .odt, .doc, .docx, image/*'/>

            <span className='file-name'>{ file ? file.name : 'Select or drop a file' }</span>

            <div className="action-buttons">
              <button className='clear-button' onClick={handleClearFile}>Clear</button>
              <button className='select-button' onClick={handleFileSelection}>Select</button>
            </div>
        </div>

        <button className='print-btn' onClick={handleFormSubit}>Print</button>
      </form>
    </>
  );
}

export default App;
