import axios from 'axios';
import { MouseEvent, useState } from 'react';
import './App.css';
import FileSelector from '../FileSelector/FileSelector';

const API_URL = 'http://192.168.15.72:5000/printer/print';

function App() {
  const [file, setFile] = useState<File | null>(null);

  const handleSetFile = (file: File | null) => (setFile(file));

  const handleFormSubit = (e: MouseEvent<HTMLButtonElement>): void => {
    e.preventDefault();
    e.stopPropagation();

    if (!file) return;

    const formData = new FormData();
    formData.append('uploaded', file);
    const headers = {
      'Content-Type': 'multipart/form-data',
      'Access-Control-Allow-Origin': '*',
    };
    axios
      .post(API_URL, formData, { headers })
      .then((res) => {
        console.log(res);
        setFile(null);
      })
      .catch((err) => console.log(err));
  };

  return (
    <>
      <h1>Rasp-Printer</h1>
      <form>
        <FileSelector file={file} handleSetFile={handleSetFile} />
        <button className='print-btn' onClick={handleFormSubit}>Print</button>
      </form>
    </>
  );
}

export default App;
